import React, {useState, useEffect } from 'react'
import { AppBar, Avatar, Typography, Toolbar, Button } from '@material-ui/core'
import axcessbook from '../../images/axcessbook.png';
import useStyles from './styles';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useHistory, useLocation } from 'react-router-dom';
import decode from 'jwt-decode';

const Navbar = () => {
    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')))
    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation()

    const logout = () => {
        dispatch({ type: 'LOGOUT'})
        history.push('/');

        setUser(null);
    }
    useEffect(() => {
        const token = user?. token;

        if(decode) {
            const decodedToken = decode(token)

            if(decodedToken.exp * 1000 < new Date().getTime()) logout()
        }

        setUser(JSON.parse(localStorage.getItem('profile')));
    }, [location]);
    return (
    <AppBar className={classes.appBar} position="static" color="inerit">
    <div className={classes.brandContainer}> 
        <Typography component={Link} to="/" className={classes.heading} variant="h2" align="center"> Your Books</Typography>
        <img className={classes.image} src={axcessbook} alt="axcessbook" height="60" />

    </div>
    <Toolbar className={classes.toolbar}>
        {user ? (
            <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user?.result.name} src={user?.result.imageUrl}>{user?.result.name.charAt(0)}</Avatar>
            <Typography className={classes.userName} variant="h6">{user.result.name}</Typography>
            <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
            </div>
        ) : (
            <Button component={Link} to="/auth" variant="contained" color="primary">Sign In</Button>
        )}

    </Toolbar>

</AppBar>
    );
};

export default Navbar;
